app.controller("DetailsController",
  ["$scope", "$routeParams","$http", "$timeout",'anchorSmoothScroll',
  function($scope, $routeParams,$http, $timeout,anchorSmoothScroll ){

    var ctrl = this;
  $('html, body').scrollTop(1); // get to top but trigger sticky to avoid layout bug
    angular.element("body")[0].className = "";
    angular.element("body").addClass("en-details");

    $(".explain-show-parts .tip").tooltipster({"theme":"tooltipster-light", "side":"right"});

    $scope.scrollTo = function(target){
      if(!document.getElementById(target)) return;
      anchorSmoothScroll.scrollTo(target, 0);
    };

    /******
     * menu toggling handler
     * > Comportement accordeon : ferme les autres, sauf son père
     * > Grise / transparence les autres
     * > Dégrise ses frères et son père si il est fermé.
     ******/

    $scope.toggle = function(obj, parent){
      if(typeof(obj.expanded) == "undefined") obj.expanded = false;

      if(!obj.expanded){
        parent.forEach(function(o,i){
          o.unselected = (o.picto != obj.picto);
          o.expanded = false;
          if(typeof(o.children) != "undefined"){
            o.children.forEach(function(c,j){
              if(c.picto == obj.picto) o.expanded = true;
              c.unselected = (c.picto != obj.picto) && o.unselected;
              c.expanded = false;
            })
          }
        });
      }else if(obj.unselected){
        //forcément un parent
        obj.children.forEach(function(c,j){
          c.unselected = false;
          c.expanded = false;
        });
        obj.unselected = false;
        obj.expanded = true;
        return;
      }else{
        parent.forEach(function(o,i){
          o.unselected = false;
          if(typeof(o.children) != "undefined"){
            o.children.forEach(function(c,j){
              c.unselected = false;
            })
          }
        });
      }


      $timeout(function(){
        var allUnselected = true;
        parent.forEach(function(o,i){
          allUnselected =  allUnselected && o.unselected;
        });
        if(allUnselected){
          parent.forEach(function(o,i){
            o.unselected = false;
          });
        }
      }, 0);

      obj.expanded = !obj.expanded;

    }

    /***
     *  Interaction handlers
     ***/

    ctrl.registerMenuCallback = function(handler, attachto){
      if(typeof(attachto.handlers) == "undefined") attachto.handlers = [];
      attachto.handlers.push(handler);
    };

    ctrl.registerHoverCallback = function(handler, attachto){
      if(typeof(attachto.hoverhandlers) == "undefined") attachto.hoverhandlers = [];
      attachto.hoverhandlers.push(handler);
    };

    ctrl.onQuadrantSelect = function(obj, parent){
      $scope.toggle(obj, parent);
      $scope.$apply();
    };

    ctrl.menuClick = function(obj, parent, type){
      $scope.toggle(obj, parent);      
      parent.handlers.forEach(function(handler){
        handler(obj,type);
      })
    }

    ctrl.menuHover = function(obj, parent){
      if(obj.unselected) return;
      parent.hoverhandlers.forEach(function(handler){
        handler(obj);
      })
    }

    ctrl.menuHoverout = function(parent){
      parent.hoverhandlers.forEach(function(handler){
        handler(null);
      })
    }

    /***
     * Data Handling
     ***/

    function parseData(data){
      for(var k in data){
        if(typeof(data[k].total) == "undefined"){
          data[k].total = data[k].children.reduce(
              function(acc, carry){
                return acc+ carry.parts.reduce(function(a,b){return a + b;},0);
              },0);
          data[k].parts = [];
          data[k].parts[0] = data[k].children.reduce(
              function(acc, carry){ return acc+ carry.parts[0]; },0
              );
          data[k].parts[1] = data[k].children.reduce(
              function(acc, carry){ return acc+ carry.parts[1]; },0
              );
          data[k].children.forEach(function(c){
            c.total = c.parts.reduce(function(a,b){return a + b;},0);
            c.color = data[k].color;
            c.lightcolor = data[k].lightcolor;
            c.darkcolor = data[k].darkcolor;
          })
        }
      }
      return data;
    }

    ctrl.showParts = false;
    ctrl.recettesData = [];
    ctrl.recettes = $http({url:'data/2017-recettes.json'})
      .then(function(response){
         ctrl.recettesData = parseData(response.data);
         return ctrl.recettesData;
      });

    ctrl.depensesData = [];
    ctrl.depenses = $http({url:'data/2017-depenses.json'})
      .then(function(response){
        ctrl.depensesData = parseData(response.data);
        return ctrl.depensesData;
      });


}]);

