angular.module('loireAtlantique.directives')
  .directive('piePart', ['d3Service', '$window', function(d3Service, $window) {
    function getWidth(){
        if(angular.element($window)[0].innerWidth <= 640) return 200;
        return 300;
    }

    function getInnerOffset(){
      if(angular.element($window)[0].innerWidth <= 640) return 40;
      return 60;
    }
    return {
      restrict: 'EA',
      scope: {
        onRenderPie : "&registerRender",
        onHidePie : "&registerHide",
        data: "=pieData"
      },
      link: function(scope, element, attrs) {
        var d3 = d3Service;
        var data = scope.data;

        var w = getWidth(),
           h = getWidth(),
           r = getWidth()/2;

        var svg = d3.select(element[0])
            .append("svg")
              .attr("width",w)
              .attr("height",h);

        var group = svg.append("g")
              .attr("transform", "translate("+r+","+r+")");


        var circle = group.append("circle")
                .attr("cx", 0)
                .attr("cy", 0)
                .attr("r", r-2)
                .style("fill", "#E6E6E6")
                .style("stroke", "#99E6D1")
                .style("stroke-width", "4");

              group.append("text")
                .attr("text-anchor", "middle")
                .attr("x", 0)
                .attr("y", 12)
                .text(data[0].figure + " %")
                .attr("class", "figure-part");

        window.onresize = function() {
            scope.$apply();
          };

        scope.$watch(function() {
          return angular.element($window)[0].innerWidth;
        }, function() {
          scope.renderFull();
        });

        scope.onRenderPie({handler : function(){ scope.renderFull(); } });
        scope.onHidePie({handler : function(){ scope.renderZero(); } });

        scope.renderFull = function() {


         var w = getWidth(),
            h = getWidth(),
            r = getWidth()/2;

         svg.attr("width",w)
               .attr("height",h);
         group.attr("transform", "translate("+r+","+r+")");

         circle.attr("r", r-2);


         var pie0 = d3.pie()
           .value(function(d) { this._current = 0; return 0; })(data);
          var pie = d3.pie()
             .value(function(d) { return d.figure; })(data);

          var arc = d3.arc()
            .innerRadius(r-getInnerOffset())
            .outerRadius(r-4);

          var arcs = d3.select(element[0]).select("svg > g").selectAll("g.slice path")
            .data(pie0);

          arcs.enter()
             .append("g")
               .attr("class", "slice")
               .append("path")
               //.transition()
               .attr("fill", function(d, i) { return d.data.color; } )
               .attr("d", function(d){return arc(d);})
              .each(function(d) { this._current = d; })

               setTimeout(function(){
                 arcs.data(pie)
                 .transition(1000)
                 .attr("d", function(d){return arc(d);})
                 .attrTween("d", arcTween);
               }, 400);



               function arcTween(a) {
                 var i = d3.interpolate(this._current, a);
                 this._current = i(0);
                 return function(t) {
                   return arc(i(t));
                 };
               }
        };

        scope.renderZero = function() {

          var w = getWidth(),
             h = getWidth(),
             r = getWidth()/2;

          svg.attr("width",w)
                .attr("height",h);
          group.attr("transform", "translate("+r+","+r+")");

          circle.attr("r", r-2);


         var pie0 = d3.pie()
           .value(function(d) { this._current = 0; return 0; })(data);


          var arc = d3.arc()
            .innerRadius(r-getInnerOffset())
            .outerRadius(r-4);

          var arcs = d3.select(element[0]).select("svg > g").selectAll("g.slice path")
            .data(pie0);

          arcs.attr("d", function(d){return arc(d);})
              .each(function(d) { this._current = d; })



        };
        //scope.render();
        scope.renderZero();

      }};
  }]);

angular.module('loireAtlantique.directives')
  .directive('pieRecettes', ['d3Service', '$window', '$timeout', function(d3Service, $window, $timeout) {

    function getWidth(){
        if(angular.element($window)[0].innerWidth <= 640) return 200;
        return 300;
    }

    function getInnerOffset(){
      if(angular.element($window)[0].innerWidth <= 640) return 40;
      return 60;
    }

    return {
      restrict: 'EA',
      scope: {
        onRenderPie : "&registerRender",
        onHidePie : "&registerHide",
        centerText: "@",
        data: "=pieData",
        tipPosition: "@"
      },
      link: function($scope, element, attrs) {
        var d3 = d3Service;
        var data = $scope.data

        var w = getWidth(),
           h = getWidth(),
           r = getWidth()/2;

        var svg = d3.select(element[0])
            .append("svg")
              .attr("width",w)
              .attr("height",h);

        var group = svg.append("g")
            .attr("transform", "translate("+r+","+r+")");

        var circle = group.append("circle")
          .attr("cx", 0).attr("cy", 0).attr("r", r - getInnerOffset())
          .style("fill", "#FFF")
          .style("stroke", "#000")
          .style("stroke-width", "4");

        group.append("text")
          .attr("class", "chart-title")
          .attr("text-anchor", "middle")
          .attr("x", 0).attr("y", 0)
          .text($scope.centerText);

        window.onresize = function() {
            $scope.$apply();
          };

        $scope.$watch(function() {
          return angular.element($window)[0].innerWidth;
        }, function() {
          $scope.renderFull();
        });

        $scope.onRenderPie({handler : function(){ $scope.renderFull(); } });
        $scope.onHidePie({handler : function(){ $scope.renderZero(); } });

        //initial rendering of the chart, reuse aftwerwards
        var pie = d3.pie()
             .value(function(d) { return d.figure; });

        // reuse arc and tween animation
        var arc = d3.arc().innerRadius(r - getInnerOffset()).outerRadius(r);
        $scope.arcTween = function (a) {
          var i = d3.interpolate(this._current, a);
          this._current = i(0);
          return function(t) {
            return arc(i(t));
          };
        }

        var arcs = group.datum(data).selectAll("path")
                        .data(pie)
                        .enter().append("path")
                        .attr("fill", function(d, i) { return d.data.color; })
                        .attr("d", arc)
                        .each(function(d) { this._current = d; });

        $scope.renderFull = function() {

          var w = getWidth();
          var r= w/2;

          svg.attr("width",w)
            .attr("height",w);

          group.attr("transform", "translate("+r+","+r+")")

          circle.attr("r", r - getInnerOffset())

          arc.innerRadius(r - getInnerOffset()).outerRadius(r);

          pie.value(function(d){  return d.figure; });
          arcs = arcs.data(pie)
          arcs.transition().duration(1000).attrTween("d", $scope.arcTween);

        };

        $scope.renderZero = function() {
          pie.value(function(d) {return 0; });
          arcs = arcs.data(pie);
          arcs.transition().duration(1000).attrTween("d", $scope.arcTween);
        };
        //$scope.render();
        $scope.renderZero();

      }};
  }]);

