angular.module('loireAtlantique.directives')
  .directive('d3Sunburn', ['d3Service', '$window', function(d3Service, $window) {
    var FR = d3.formatLocale(fr_FR);
    return {
      restrict: 'EA',
      scope: {
        onMenuSelect: '&registerMenuCallback',
        onMenuHover: '&registerHoverCallback',
        selectMenu: '&onQuadrantSelect',
        dataPromise:"=d3Sunburn",
        showParts:"=showParts"
      },
      template: '<div class="graph"><div class="report-text" ng-bind-html="reportText"></div></div>',
      link: function($scope, element, attrs) {
        var d3 = d3Service;

        /**
         *  INIT
         **/

        var graphElement = d3.select(element[0]).select(".graph");
        var graphWidth = Math.min(540, element.find(".graph").width());
        $scope.margin = {top: graphWidth/2,
                         right: graphWidth/2,
                         bottom: graphWidth/2,
                         left: graphWidth/2
                       };
        $scope.radius = Math.min($scope.margin.top, $scope.margin.right, $scope.margin.bottom, $scope.margin.left) - 10;
        $scope.maxValue = 2000000000;
        $scope.total = 0;
        $scope.type = "all";
        $scope.selectedQuadrant = null;

        var svg = graphElement
            .append("svg")
            .attr("width", $scope.margin.left + $scope.margin.right)
            .attr("height", $scope.margin.top + $scope.margin.bottom)
            .append("g")
            .attr("transform", "translate(" + $scope.margin.left + "," + $scope.margin.top + ")")
            ;

        /**
         *  Responsive
         **/

        window.onresize = function() {
            $scope.$apply();
        };
        $scope.$watch(function() {
          // return angular.element($window)[0].innerWidth;
          return d3.select(element[0]).innerWidth;
        }, function() {
          $scope.render( $scope.getData($scope.data, $scope.type) );
        });

        $scope.$watch('showParts', function(){
          $scope.render( $scope.getData($scope.data, $scope.type) );
          if($scope.selectedQuadrant != null){
            $scope.onQuadrantSelected($scope.selectedQuadrant, $scope.type);
          }else{
            if($scope.showParts){
              defaultSplittedText();
            }else{
              defaultText();
            }
          }

        });

        /**
         *  Event handling
         **/

        $scope.onQuadrantClick = function(d,i){
          $scope.selectMenu({obj:d,parent:$scope.data});
          $scope.onQuadrantSelected(d, $scope.type);
          $scope.$apply();
        }

        $scope.onQuadrantHover = function(d){
          if(d.unselected) return;
          $scope.onQuadrantHovered(d);
          $scope.$apply();
        }
        $scope.onQuadrantHoverout = function(){
          $scope.onQuadrantHovered(null);
          $scope.$apply();
        }


        $scope.onQuadrantSelected = function(datum, type){

          clearText();
          if(type != $scope.type ){
            $scope.type = type;
            $scope.render( $scope.getData($scope.data, $scope.type) );
          }

          if(datum.expanded){
            $scope.selectedQuadrant = datum;

            if($scope.showParts){

              $scope.center.selectAll(".center-top").transition().attr("fill",datum.darkcolor);
              $scope.center.selectAll(".center-bottom").transition().attr("fill",datum.lightcolor);
              quadrantSplittedText(datum);
            }else{
              $scope.center.transition().attr("fill",datum.color);
              quadrantText(datum);
            }
          }else{
            $scope.selectedQuadrant = null;

            if($scope.showParts){
              $scope.center.selectAll(".center-top").transition().attr("fill","#b8b7b7");
              $scope.center.selectAll(".center-bottom").transition().attr("fill","#e2e1e1");
              defaultSplittedText();
            }else{
              $scope.center.transition().attr("fill","white");
              defaultText();
            }
          }
          $scope.quadrants.selectAll(".quadrant-part")
                .transition()
                .attr("fill", function(d, i){
                  if(d.color == "transparent") return "transparent";
                  if(d.d.unselected){
                    var color = d3.color(d.color)
                        color.opacity = 0.5;
                        return color;
                  }else{
                    return d.color;
                  }
                });


          // after a selection and a deselection all arrow get selected
          // this fix it
          var allSelected = $scope.quadrants.selectAll(".arrow")
            .data().every(function(d,i,a){return !d.d.unselected});
          if(allSelected){
            $scope.quadrants.selectAll(".arrow")
              .data().forEach(function(d){d.d.unselected = true});
          }

          // show selected quadrant's arrow
          $scope.quadrants.selectAll(".arrow")
                .transition()
                .attr("fill", function(d, i){
                  if(d.d.unselected){
                   return "rgba(0,0,0,0)"
                  }else{
                    return "rgba(0,0,0,255)";
                  }
                });
        };
        $scope.onQuadrantHovered = function(datum){

          if(datum == null && $scope.selectedQuadrant != null)
            datum = $scope.selectedQuadrant;

          if(datum == null){
            if($scope.showParts){
              $scope.center.selectAll(".center-top").transition().attr("fill","#b8b7b7");
              $scope.center.selectAll(".center-bottom").transition().attr("fill","#e2e1e1");
              defaultSplittedText();
            }else{
              $scope.center.transition().attr("fill","white");
              defaultText();
            }
          }else{
            if($scope.showParts){
              $scope.center.selectAll(".center-top").transition().attr("fill",datum.darkcolor);
              $scope.center.selectAll(".center-bottom").transition().attr("fill",datum.lightcolor);
              quadrantSplittedText(datum);
            }else{
              $scope.center.transition().attr("fill",datum.color);
              quadrantText(datum);
            }
          }
        };


        /**
         *  Data transformation for D3 / Zoom / etc.
         **/
        $scope.getData = function(data, type){
          if(type == "all") return data;
          if(type == "child"){

            return data.reduce(function(acc, quad){
                return acc.concat(
                  quad.children.map(function(child){
                      child.color = quad.color;
                      child.lightcolor = quad.lightcolor;
                      child.darkcolor = quad.darkcolor;
                      return child;
                  })
                );
            },[]);

          }
        };

        /**
         *  Data loading by promise
         **/
        $scope.dataPromise.then(function(data){
          $scope.data = data;
          $scope.onMenuSelect({handler : function(d,type){
              $scope.onQuadrantSelected(d, type);
            }, attachto: $scope.data
          });

          $scope.onMenuHover({handler : function(d){
              $scope.onQuadrantHovered(d);
            }, attachto: $scope.data
          });
          $scope.innerRadius = Math.min(graphWidth/5, 100);
          $scope.initialRender();
          $scope.render( $scope.getData(data, "all") );

          if($scope.showParts){
            defaultSplittedText();
          }else{
            defaultText();
          }
        });


        /**
         * ReportText rendering
         **/

        function defaultText(){
          $scope.reportText =
              "<span class='number'>"+
              (Math.round($scope.total/1000000)/1000).toFixed(3)+
              "</span>"+
              "<span class='unit'>"+
              "Md €"+
              "</span>"+
              "<span class='period'>"+
              "Budget prévisionnel <br> 2021"+
              "</span>";
        }

        function clearText(){
          $scope.reportText = "";
        }

        function quadrantText(d){
          var percent = (d.total*100/$scope.total);
          $scope.reportText =
              "<img class='picto' src='img/"+d.picto+".svg'>"+
              "<span class='category'>"+
              d.name+
              "</span>"+
              "<span class='number'>"+
              percent.toFixed(1)+
              " %</span>"+
              "<span class='subnumber'>"+
              FR.format(',')(d.total.toFixed(0))+" €"+
              "</span>";
        }

        function defaultSplittedText(){

          var percentTop = ($scope.totalParts[1]*100/$scope.total);
          var percentBottom = ($scope.totalParts[0]*100/$scope.total);

          $scope.reportText =
              "<span class='percent-top'>" + percentTop.toFixed(1) + " %</span>" +
              "<span class='number-top'>" + FR.format(',')($scope.totalParts[1].toFixed(0)) + " €</span>" +
              "<span class='legend-top'>INVESTISSEMENT</span>" +
              "<span class='legend-bottom'>FONCTIONNEMENT</span>" +
              "<span class='percent-bottom'>" + percentBottom.toFixed(1)+" %</span>" +
              "<span class='number-bottom'>" + FR.format(',')($scope.totalParts[0].toFixed(0)) + " €</span>"

        }

        function quadrantSplittedText(d){
          var percentTop = (d.parts[1]*100/d.total);
          var percentBottom = (d.parts[0]*100/d.total);

          $scope.reportText =
              "<span class='percent-top'>" + percentTop.toFixed(1) + " %</span>" +
              "<span class='number-top'>" + FR.format(',')(d.parts[1].toFixed(0)) + " €</span>" +
              "<span class='legend-top'>INVESTISSEMENT</span>" +
              "<span class='legend-bottom'>FONCTIONNEMENT</span>" +
              "<span class='percent-bottom'>" + percentBottom.toFixed(1)+" %</span>" +
              "<span class='number-bottom'>" + FR.format(',')(d.parts[0].toFixed(0)) + " €</span>"
        }

        /**
         *  Base rendering
         **/
        $scope.initialRender = function(){
          //outer border
            svg.append("circle")
               .attr("r", $scope.radius)
               .attr("fill","transparent")
               .attr("stroke","black")
               .attr("stroke-width",2)
               ;
            svg.append("g").append("circle")
               .attr("r", $scope.innerRadius)
               .attr("fill","#f4f4f4")
        }

        /**
         *  Full Rendering
         *  This is where animation should happen
         **/

         function rad2deg(radians){
            return radians * (180/Math.PI);
         }

        $scope.render = function(data) {
            if(typeof(data) == "undefined") return;
            var stepAngle = 2*Math.PI / data.length;

            $scope.total    = data.reduce(function(acc,carry){return acc + carry.total;},0);
            $scope.maxValue = d3.max(data, function(d){return d.total*1.25;});

            if($scope.showParts){
              $scope.totalParts =[
                data.reduce(function(acc,carry){return acc + carry.parts[0];},0),
                data.reduce(function(acc,carry){return acc + carry.parts[1];},0)
              ];
            }

            //center white circle (assign an object : will be interactive)
            svg.selectAll(".center").remove();
            if($scope.showParts){

              $scope.center = svg.append("g").attr("class", "center")

              $scope.center.append("path").attr("class","center-top")
               .attr("d", d3.arc()
                            .innerRadius(0)
                            .outerRadius($scope.innerRadius - 4 )
                            .startAngle(-Math.PI/2) //convert from degs to radians
                            .endAngle(Math.PI/2))
               .attr("fill","#b8b7b7")
               ;

              $scope.center.append("path").attr("class","center-bottom")
                .attr("d", d3.arc()
                            .innerRadius(0)
                            .outerRadius($scope.innerRadius - 4)
                            .startAngle(Math.PI/2) //convert from degs to radians
                            .endAngle(3*Math.PI/2))
               .attr("fill","#e2e1e1")
               ;

            }else{
              $scope.center = svg.append("g").attr("class", "center").append("circle")
               .attr("r", $scope.innerRadius - 4)
               .attr("fill","white")
               .attr("stroke","white")
               .attr("stroke-width",1.5)
               ;
            }


            //graduations
            var stepRadius = ($scope.radius - $scope.innerRadius) / 8;
            svg.selectAll(".graduation").remove();
            for(i = 1 ; i <= 8 ; i++){
              svg.append("circle")
                 .attr("class", "graduation")
                 .attr("r",  $scope.innerRadius + i*stepRadius)
                 .attr("fill","transparent")
                 .attr("stroke","#d3d3d3")
                 .attr("stroke-width",0.5)
                 ;
            }

            //quadrants
            var quadrant = d3.arc()
                        .innerRadius(function(d,i){
                          return $scope.innerRadius + 1;
                        })
                        .outerRadius(function(d,i){
                          return $scope.innerRadius + 1 + (d.total/$scope.maxValue) * ($scope.radius - $scope.innerRadius);
                        })
                        .startAngle(function(d,i){
                          return i*stepAngle;
                        })
                        .endAngle(function(d,i){
                          return (i+1)*stepAngle;
                        });

            var lowerquadrant = d3.arc()
                        .innerRadius(function(d,i){
                          return $scope.innerRadius + 1;
                        })
                        .outerRadius(function(d,i){
                          return $scope.innerRadius + 1 + (d.parts[0]/$scope.maxValue) * ($scope.radius - $scope.innerRadius);
                        })
                        .startAngle(function(d,i){
                          return i*stepAngle;
                        })
                        .endAngle(function(d,i){
                          return (i+1)*stepAngle;
                        });
            var higherquadrant = d3.arc()
                        .innerRadius(function(d,i){
                          return $scope.innerRadius + 1 + (d.parts[0]/$scope.maxValue) * ($scope.radius - $scope.innerRadius);
                        })
                        .outerRadius(function(d,i){
                          return $scope.innerRadius + 1 + (d.total/$scope.maxValue) * ($scope.radius - $scope.innerRadius);
                        })
                        .startAngle(function(d,i){
                          return i*stepAngle;
                        })
                        .endAngle(function(d,i){
                          return (i+1)*stepAngle;
                        });
            var transquadrant = d3.arc()
                        .innerRadius(function(d,i){
                          return $scope.innerRadius + 1 + (d.total/$scope.maxValue) * ($scope.radius - $scope.innerRadius);
                        })
                        .outerRadius(function(d,i){
                          return $scope.radius;
                        })
                        .startAngle(function(d,i){
                          return i*stepAngle;
                        })
                        .endAngle(function(d,i){
                          return (i+1)*stepAngle;
                        });

            svg.selectAll(".quadrant").remove();
            $scope.quadrants = svg.selectAll(".quadrant")
               .data(data)
               .enter()
               .append("g").attr("class", "quadrant")



            // draw arrow for selected quadrant
            var offset = Math.PI/2; // why ???
            $scope.quadrants.append("polygon")
              .datum(function(d,i){return {d: d}})
              .attr("points", function(d,i){
                var angle = (i+.5)*stepAngle -offset;
                var sommet_x = Math.cos(angle) * ($scope.innerRadius + 1)
                var sommet_y = Math.sin(angle) * ($scope.innerRadius + 1);

                var base_x = Math.cos(angle) * ($scope.innerRadius - 4);
                var base_y = Math.sin(angle) * ($scope.innerRadius - 4);

                var semi_side = 6;
                var left_x = base_x + Math.cos(angle - Math.PI/2) * semi_side;
                var left_y = base_y + Math.sin(angle - Math.PI/2) * semi_side;
                var right_x = base_x + Math.cos(angle + Math.PI/2) * semi_side;
                var right_y = base_y + Math.sin(angle + Math.PI/2) * semi_side;

                return sommet_x + "," + sommet_y + " " + left_x +","+left_y + " " + right_x + "," + right_y;

              })
              .attr("style", "stroke-width:4px;")
              .attr("fill", "rgba(0,0,0,0)")
              .attr("class", "arrow");


            if($scope.showParts){
              $scope.quadrants.append("path")
               .datum(function(d,i){
                return {
                  total: d.total,
                  color: d.lightcolor,
                  parts: d.parts,
                  picto: d.picto,
                  name: d.name,
                  d: d,
                }
               })
               .attr("d",lowerquadrant).attr("class","quadrant-part")
               .attr("fill", function(d, i){
                  return d.d.lightcolor;
               })
               .on("click", function(d, i){
                $scope.onQuadrantClick(d.d,i);
               })
               .on("mouseover", function(d, i){
                $scope.onQuadrantHover(d.d);
               })
               .on("mouseout", function(d, i){
                $scope.onQuadrantHoverout();
               })

               ;

              $scope.quadrants.append("path")
               .datum(function(d,i){
                return {
                  total: d.total,
                  color: d.darkcolor,
                  parts: d.parts,
                  picto: d.picto,
                  name: d.name,
                  d: d,
                }
               })
               .attr("d",higherquadrant).attr("class","quadrant-part")
               .attr("fill", function(d, i){
                  return d.d.darkcolor;
               })
               .on("click", function(d, i){
                $scope.onQuadrantClick(d.d,i);
               })
               .on("mouseover", function(d, i){
                $scope.onQuadrantHover(d.d);
               })
               .on("mouseout", function(d, i){
                $scope.onQuadrantHoverout();
               })

               ;

            }else{
              $scope.quadrants.append("path")
               .datum(function(d,i){
                return {
                  total: d.total,
                  color: d.color,
                  picto: d.picto,
                  name: d.name,
                  d: d,
                }
               })
               .attr("d",quadrant).attr("class","quadrant-part")
               .attr("fill", function(d, i){
                  return d.color;
               })
               .on("click", function(d, i){
                $scope.onQuadrantClick(d.d,i);
               })
               .on("mouseover", function(d, i){
                $scope.onQuadrantHover(d.d);
               })
               .on("mouseout", function(d, i){
                $scope.onQuadrantHoverout();
               })

               ;
            }
              $scope.quadrants.append("path")
               .datum(function(d,i){
                return {
                  total: d.total,
                  color: "transparent",
                  picto: d.picto,
                  name: d.name,
                  d: d,
                }
               })
               .attr("d",transquadrant).attr("class","quadrant-part")
               .attr("fill", function(d, i){
                  return "transparent";
               })
               .on("click", function(d, i){
                $scope.onQuadrantClick(d.d,i);
               })
               .on("mouseover", function(d, i){
                $scope.onQuadrantHover(d.d);
               })
               .on("mouseout", function(d, i){
                $scope.onQuadrantHoverout();
               })


               ;

            // line to separate the gradients
            svg.selectAll(".quadrant-graduation").remove();
            $scope.quadrantGraduations = svg.selectAll(".quadrant-graduation")
               .data(data)
               .enter()
               .append("g").attr("class", "quadrant-graduation")
               .append("line")
                 .attr("x1",function(d, i){ return 0; } )
                 .attr("y1",function(d, i){ return -$scope.innerRadius; } )
                 .attr("x2",function(d, i){ return 0; } )
                 .attr("y2",function(d, i){ return -$scope.radius; } )
                 .attr("stroke","#c3c3c3")
                 .attr("stroke-width",1)
                 // .transition()
                 // .attr("x1",function(d, i){ return $scope.innerRadius*Math.cos(i*stepAngle-Math.PI/2); } )
                 // .attr("y1",function(d, i){ return $scope.innerRadius*Math.sin(i*stepAngle-Math.PI/2); } )
                 // .attr("x2",function(d, i){ return $scope.radius*Math.cos(i*stepAngle-Math.PI/2); } )
                 // .attr("y2",function(d, i){ return $scope.radius*Math.sin(i*stepAngle-Math.PI/2); } )
                 .attr("transform", function(d, i){ return "rotate("+rad2deg(i*stepAngle)+")";})
                 // .duration(5000)
                 ;
        };
      }};
  }]);

angular.module('loireAtlantique.filters')
.filter('thousandSeparator', function() {

    return function (input) {

      var output = "";
      input = input + "";
      var charCount = 0;
      for(var i = input.length-1; i>=0; i--){
        output = input[i] + output;
        charCount++;
        if(i !== 0 && i !== (input.length-1) && (charCount%3)===0){
          output = " " + output;
        }
      }

      return output;

    }

});

